'use strict';

const ApiBuilder = require('claudia-api-builder');
const fbSetup = require('./facebook/setup');
const slackSetup = require('./slack/setup');
const telegramSetup = require('./telegram/setup');
const skypeSetup = require('./skype/setup');
const twilioSetup = require('./twilio/setup');
const kikSetup = require('./kik/setup');
const groupmeSetup = require('./groupme/setup');
const lineSetup = require('./line/setup');
const viberSetup = require('./viber/setup');
const alexaSetup = require('./alexa/setup');
const fbTemplate = require('./facebook/format-message');
const slackTemplate = require('./slack/format-message');
const telegramTemplate = require('./telegram/format-message');
const viberTemplate = require('./viber/format-message');
const skypeTemplate = require('./skype/format-message');
const AlexaTemplate = require('alexa-message-builder');
const slackDelayedReply = require('./slack/delayed-reply');

let logError = function (err) {
  console.error(err);
};

module.exports = function botBuilder(messageHandler, options, optionalLogError) {
  logError = optionalLogError || logError;

  const api = new ApiBuilder(),
    messageHandlerPromise = function (message, originalApiBuilderRequest) {
      return Promise.resolve(message).then(message => messageHandler(message, originalApiBuilderRequest));
    };

  api.get('/', () => 'Ok');

  let isEnabled = function isEnabled(platform) {
    return !options || !options.platforms || options.platforms.indexOf(platform) > -1;
  };

  if (isEnabled('facebook')) {
    fbSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('slackSlashCommand')) {
    slackSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('telegram')) {
    telegramSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('skype')) {
    skypeSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('twilio')) {
    twilioSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('kik')) {
    kikSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('groupme')) {
    groupmeSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('line')) {
    lineSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('viber')) {
    viberSetup(api, messageHandlerPromise, logError);
  }
  if (isEnabled('alexa')) {
    alexaSetup(api, messageHandlerPromise, logError);
  }

  return api;
};

module.exports.fbTemplate = fbTemplate;
module.exports.slackTemplate = slackTemplate;
module.exports.telegramTemplate = telegramTemplate;
module.exports.viberTemplate = viberTemplate;
module.exports.skypeTemplate = skypeTemplate;
module.exports.AlexaTemplate = AlexaTemplate;
module.exports.slackDelayedReply = slackDelayedReply;

'use strict';

const crypto = require('crypto');
const prompt = require('souffleur');
const rp = require('minimal-request-promise');
const fbReply = require('./reply');
const fbParse = require('./parse');
const validateFbRequestIntegrity = require('./validate-integrity');
const color = require('../console-colors');

module.exports = function fbSetup(api, bot, logError, optionalParser, optionalResponder) {
  let parser = optionalParser || fbParse;
  let responder = optionalResponder || fbReply;

  api.get('/facebook', request => {
    if (request.queryString['hub.verify_token'] === request.env.facebookVerifyToken)
      return request.queryString['hub.challenge'];

    logError(`Facebook can't verify the token. It expected '${request.env.facebookVerifyToken}', but got '${request.queryString['hub.verify_token']}'. Make sure you are using the same token you set in 'facebookVerifyToken' stage env variable.`);
    return 'Error';
  }, {success: {contentType: 'text/plain'}});

  api.post('/facebook', request => {
    // We are doing verification if FB Secret exist in env because we don't want to break old bots that forgot to add it
    if (request.env.facebookAppSecret && !validateFbRequestIntegrity(request))
      return Promise.reject('X-Hub-Signatures does not match');

    let arr = [].concat.apply([], request.body.entry.map(entry => entry.messaging));
    let fbHandle = parsedMessage => {
      if (parsedMessage) {
        var recipient = parsedMessage.sender;

        return Promise.resolve(parsedMessage).then(parsedMessage => bot(parsedMessage, request))
          .then(botResponse => responder(recipient, botResponse, request.env.facebookAccessToken))
          .catch(logError);
      }
    };

    return Promise.all(arr.map(message => fbHandle(parser(message))))
      .then(() => 'ok');
  });

  api.addPostDeployStep('facebook', (options, lambdaDetails, utils) => {
    return Promise.resolve().then(() => {
      return utils.apiGatewayPromise.getStagePromise({
        restApiId: lambdaDetails.apiId,
        stageName: lambdaDetails.alias
      }).then(data => {
        if (options['configure-fb-bot']) {
          let token, pageAccessToken;

          return Promise.resolve().then(() => {
            if (data.variables && data.variables.facebookVerifyToken)
              return data.variables.facebookVerifyToken;

            return crypto.randomBytes(8);
          })
          .then(rawToken => {
            token = rawToken.toString('base64').replace(/[^A-Za-z0-9]/g, '');
            return utils.apiGatewayPromise.createDeploymentPromise({
              restApiId: lambdaDetails.apiId,
              stageName: lambdaDetails.alias,
              variables: {
                facebookVerifyToken: token
              }
            });
          })
          .then(() => {
            console.log(`\n\n${color.green}Facebook Messenger setup${color.reset}\n`);
            console.log(`\nFollowing info is required for the setup, for more info check the documentation.\n`);
            console.log(`\nYour webhook URL is: ${color.cyan}${lambdaDetails.apiUrl}/facebook${color.reset}\n`);
            console.log(`Your verify token is: ${color.cyan}${token}${color.reset}\n`);

            return prompt(['Facebook page access token', 'Facebook App Secret']);
          })
          .then(results => {
            console.log('\n');
            pageAccessToken = results['Facebook page access token'];
            const deployment = {
              restApiId: lambdaDetails.apiId,
              stageName: lambdaDetails.alias,
              variables: {
                facebookAccessToken: pageAccessToken,
                facebookAppSecret: results['Facebook App Secret']
              }
            };

            if (!data.variables || !data.variables.facebookAppSecret)
              console.log(`\n${color.yellow}Deprecation warning:${color.reset} your bot is not using facebook validation. Please re-run with --configure-fb-bot to set it. This will become mandatory in the next major version. See https://github.com/claudiajs/claudia-bot-builder/blob/master/docs/API.md#message-verification for more information.\n`);

            return utils.apiGatewayPromise.createDeploymentPromise(deployment);
          })
          .then(() => rp.post(`https://graph.facebook.com/v2.6/me/subscribed_apps?access_token=${pageAccessToken}`));
        }
      });
    })
      .then(() => `${lambdaDetails.apiUrl}/facebook`);
  });
};

'use strict';

const rp = require('minimal-request-promise'),
  breakText = require('../breaktext');

module.exports = function fbReply(recipient, message, fbAccessToken) {
  var sendSingle = function sendSingle (message) {
      if (typeof message === 'object' && typeof message.claudiaPause === 'number') {
        return new Promise(resolve => setTimeout(resolve, parseInt(message.claudiaPause, 10)));
      }
      const messageBody = {
        recipient: {
          id: recipient
        }
      };
      if (message.hasOwnProperty('notification_type')) {
        messageBody.notification_type = message.notification_type;
        delete message.notification_type;
      }
      if (message.hasOwnProperty('sender_action')) {
        messageBody.sender_action = message.sender_action;
      } else {
        messageBody.message = message;
      }
      const options = {
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(messageBody)
      };
      return rp.post(`https://graph.facebook.com/v2.6/me/messages?access_token=${fbAccessToken}`, options);
    },
    sendAll = function () {
      if (!messages.length) {
        return Promise.resolve();
      } else {
        return sendSingle(messages.shift()).then(sendAll);
      }
    },
    messages = [];

  function breakTextAndReturnFormatted(message) {
    return breakText(message, 640).map(m => ({ text: m }));
  }

  if (typeof message === 'string') {
    messages = breakTextAndReturnFormatted(message);
  } else if (Array.isArray(message)) {
    message.forEach(msg => {
      if (typeof msg === 'string') {
        messages = messages.concat(breakTextAndReturnFormatted(msg));
      } else {
        messages.push(msg);
      }
    });
  } else if (!message) {
    return Promise.resolve();
  } else {
    messages = [message];
  }
  return sendAll();
};

/*global module*/
var chunk = function (tx, callback) {
  var prev = 0,
    re = /\s/gi,
    chunk;
  while (re.exec(tx)) {
    chunk = tx.slice(prev, re.lastIndex - 1);
    callback(chunk);
    prev = re.lastIndex - 1;
  }
  if (prev < tx.length) {
    callback(tx.slice(prev));
  }
};

module.exports = function (text, limit) {
  'use strict';
  var lines = [],
    currentLine = [],
    currentLineLength = 0,
    closeLine = function () {
      if (currentLineLength) {
        lines.push(currentLine.join('').trim());
        currentLine = [];
        currentLineLength = 0;
      }
    },
    processChunk = function (chunk) {
      var index;
      if (currentLineLength + chunk.length > limit) {
        closeLine();
      }
      if (chunk.length > limit) {
        for (index = 0; index < chunk.length; index += limit) {
          processChunk(chunk.slice(index, index + limit));
        }
      } else {
        currentLineLength += chunk.length;
        currentLine.push(chunk);
      }
    };

  if (text === '') {
    return [''];
  }

  chunk(text, processChunk);
  closeLine();

  return lines;
};

