      expect(lambdaContextSpy.done).toHaveBeenCalledWith(null, jasmine.objectContaining({body: '"Ok"'}));
    }).then(done, done.fail);
  });
  [
    {
      name: 'facebook',
      path: 'facebook',
      methods: ['GET', 'POST']
    },
    {
      name: 'slackSlashCommand',
      path: 'slack/slash-command',
      methods: ['GET', 'POST']
    },
    {
      name: 'telegram',
      path: 'telegram',
      methods: ['POST']
    },
    {
      name: 'skype',
      path: 'skype',
      methods: ['POST']
    },
    {
      name: 'twilio',
      path: 'twilio',
      methods: ['POST']
    },
    {
      name: 'kik',
      path: 'kik',
      methods: ['POST']
    },
    {
      name: 'groupme',
      path: 'groupme',
      methods: ['POST']
    },
    {
      name: 'viber',
      path: 'viber',
      methods: ['POST']
    }
  ].forEach(platform => {
