var parse = require('../../lib/facebook/parse');
describe('Facebook parse', () => {
  it('returns nothing if the format is invalid', () => {
    expect(parse('string')).toBeUndefined();
    expect(parse()).toBeUndefined();
    expect(parse(false)).toBeUndefined();
    expect(parse(123)).toBeUndefined();
    expect(parse({})).toBeUndefined();
    expect(parse([1, 2, 3])).toBeUndefined();
  });
  it('returns false if the message or sender are missing', () => {
    expect(parse({sender: 'tom'})).toBeUndefined();
    expect(parse({message: 'pete'})).toBeUndefined();
  });
  it('returns a parsed object when there message and sender are present', () => {
    var msg = {sender: {id: 'tom'}, message: { text: 'Hello' }};
    expect(parse(msg, {})).toEqual({ sender: 'tom', text: 'Hello', originalRequest: msg, type: 'facebook'});
  });
  it('returns a parsed object for postback messages', () => {
    var msg = {
      sender: { id: '12345' },
      recipient: { id: '67890' },
      timestamp: 1465558466933,
      postback: { payload: 'POSTBACK' }
    };
    expect(parse(msg)).toEqual({
      sender: '12345',
      text: 'POSTBACK',
      originalRequest: msg,
      postback: true,
      type: 'facebook'
    });
  });
  it('returns a parsed object for a quick reply', () => {
    var msg = {
      sender: { id: '12345' },
      recipient: { id: '67890' },
      timestamp: 1465558466933,
      message: {
        mid: 'mid.1464990849238:b9a22a2bcb1de31773',
        seq: 69,
        quick_reply: {
          payload: 'QUICK_REPLY'
        }
      }
    };
    expect(parse(msg)).toEqual({
      sender: '12345',
      text: 'QUICK_REPLY',
      originalRequest: msg,
      type: 'facebook',
      postback: true
    });
  });
  it('does not parse the object if it is delivery report', () => {
    var msg = {
      sender: { id: '12345' },
      recipient: { id: '67890' },
      timestamp: 1465558466933,
      delivery: {
        mids: ['mid.1458668856218:ed81099e15d3f4f233'],
        watermark: 1458668856253,
        seq: 37
      }
    };
    expect(parse(msg)).toBeFalsy();
  });
  it('does not parse the object if it is read report', () => {
    var msg = {
      sender: { id: '12345' },
      recipient: { id: '67890' },
      timestamp: 1465558466933,
      read: {
        watermark: 1458668856253,
        seq: 38
      }
    };
    expect(parse(msg)).toBeFalsy();
  });
  it('does not parse the object if it is an echo message', () => {
    var msg = {
      sender: {
        id: '12345'
      },
      recipient: {
        id: '54321'
      },
      timestamp: 1483413621558,
      message: {
        is_echo: true,
        app_id: 314159,
        mid: 'mid.1483413621558:a9dc28cb84',
        seq: 1022,
        text: 'Some text'
      }
    };
    expect(parse(msg)).toBeFalsy();
  });
});

/*global describe, it, expect, require, jasmine, beforeEach */
var reply = require('../../lib/facebook/reply'),
  https = require('https');
describe('Facebook Reply', () => {
  'use strict';
  beforeEach(() =>{
    jasmine.DEFAULT_TIMEOUT_INTERVAL = 5000;
  });
  it('includes the token in the path request', done => {
    https.request.pipe(callOptions => {
      expect(callOptions).toEqual(jasmine.objectContaining({
        method: 'POST',
        hostname: 'graph.facebook.com',
        path: '/v2.6/me/messages?access_token=ACCESS123',
        headers: {
          'Content-Type': 'application/json'
        }
      }));
      done();
    });
    reply('user123', 'Hi there', 'ACCESS123');
  });
  it('sends string messages as a text object', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        recipient: {
          id: 'user123'
        },
        message: {
          text: 'Hi there'
        }
      });
      done();
    });
    reply('user123', 'Hi there', 'ACCESS123');
  });
  it('sends large text messages split into several', done => {
    var longLongMessage = new Array(201).join('blok ');

    https.request.pipe(function () {
      this.respond('200', 'OK', 'Hi there');
    });

    reply('user123', longLongMessage, 'ACCESS123').then(() => {
      expect(https.request.calls.length).toEqual(2);
      expect(JSON.parse(https.request.calls[0].args[0].body)).toEqual({
        recipient: {
          id: 'user123'
        },
        message: {
          text: new Array(640/5).join('blok ') + 'blok'
        }
      });
      expect(JSON.parse(https.request.calls[1].args[0].body)).toEqual({
        recipient: {
          id: 'user123'
        },
        message: {
          text: new Array((1000 - 640)/5).join('blok ') + 'blok'
        }
      });
    }).then(done, done.fail);
  });
  it('sends requests in sequence', done => {
    var fiveHundred = new Array(101).join('blok ');

    https.request.pipe(() => {
      Promise.resolve().then(() => {
        expect(https.request.calls.length).toEqual(1);
      }).then(done);
    });

    reply('user123', fiveHundred, 'ACCESS123');
  });
  describe('when an array is passed', () => {
    it('does not send the second request until the first one completes', done => {
      let answers = ['foo', 'bar'];
      https.request.pipe(() => {
        Promise.resolve().then(() => {
          expect(https.request.calls.length).toEqual(1);
        }).then(done);
      });
      reply('user123', answers, 'ACCESS123');
    });
    it('sends the requests in sequence', done => {
      let answers = ['foo', 'bar'];
      https.request.pipe(function () {
        this.respond('200', 'OK');
        if (https.request.calls.length === 2) {
          expect(JSON.parse(https.request.calls[0].body[0])).toEqual({recipient:{id:'user123'},message:{text:'foo'}});
          expect(JSON.parse(https.request.calls[1].body[0])).toEqual({recipient:{id:'user123'},message:{text:'bar'}});
          done();
        }
      });
      reply('user123', answers, 'ACCESS123');

    });

  });
  it('sends complex messages without transforming into a text object', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        recipient: {
          id: 'user123'
        },
        message: {
          template: 'big',
          contents: {
            title: 'red'
          }
        }
      });
      done();
    });
    reply('user123', {template: 'big', contents: { title: 'red'} }, 'ACCESS123');
  });
  it('does not send a message if message is not provided', () => {
    reply('user123', null, 'ACCESS123')
      .then(() => {
        expect(https.request.calls.length).toBe(0);
      });

    reply('user123', false, 'ACCESS123')
      .then(() => {
        expect(https.request.calls.length).toBe(0);
      });

    reply('user123', undefined, 'ACCESS123')
      .then(() => {
        expect(https.request.calls.length).toBe(0);
      });
  });
  it('sets a typing action if "sender_action" is passed', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        recipient: {
          id: 'user123'
        },
        sender_action: 'typing_on'
      });
      done();
    });
    reply('user123', { sender_action: 'typing_on' }, 'ACCESS123');
  });
  it('does not resolve before the https endpoint responds', done => {
    https.request.pipe(done);
    reply('user123', {template: 'big', contents: { title: 'red'} }, 'ACCESS123').then(done.fail, done.fail);
  });
  it('resolves when the https endpoint responds with 200', done => {
    https.request.pipe(function () {
      this.respond('200', 'OK', 'Hi there');
    });
    reply('user123', {template: 'big', contents: { title: 'red'} }, 'ACCESS123').then(done, done.fail);
  });
});

/*global require, describe, it, expect, beforeEach, jasmine*/
'use strict';
var underTest = require('../../lib/facebook/setup');
describe('Facebook setup', () => {
  var api, bot, logError, parser, responder, botPromise, botResolve, botReject;
  beforeEach(() => {
    api = jasmine.createSpyObj('api', ['get', 'post', 'addPostDeployStep']);
    botPromise = new Promise((resolve, reject) => {
      botResolve = resolve;
      botReject = reject;
    });
    bot = jasmine.createSpy().and.returnValue(botPromise);
    parser = jasmine.createSpy();
    logError = jasmine.createSpy();
    responder = jasmine.createSpy();
    underTest(api, bot, logError, parser, responder);
  });
  describe('token validator', () => {
    it('wires the GET request for facebook to a token validator', () => {
      expect(api.get.calls.count()).toEqual(1);
      expect(api.get).toHaveBeenCalledWith('/facebook', jasmine.any(Function), {success: {contentType: 'text/plain'}});
    });
    it('replies with hub.challenge when tokens match', () => {
      let handler = api.get.calls.argsFor(0)[1],
        result = handler({
          queryString: {
            'hub.verify_token': '12345',
            'hub.challenge': 'XXAA'
          }, env: {
            facebookVerifyToken: '12345'
          }
        });
      expect(result).toEqual('XXAA');
    });
    it('replies with Error when tokens do not match', () => {
      let handler = api.get.calls.argsFor(0)[1],
        result = handler({
          queryString: {
            'hub.verify_token': '12345',
            'hub.challenge': 'XXAA'
          }, env: {
            facebookVerifyToken: '12346'
          }
        });
      expect(result).toEqual('Error');
    });
  });
  describe('message processor', () => {
    const singleMessageTemplate = {
      'object':'page',
      'entry':[{
        'id': 'PAGE_ID',
        'time': 1457764198246,
        'messaging':[
          { 'A': 'B' }
        ]
      }]
    };
    it('wires the POST request for facebook to the message processor', () => {
      expect(api.post.calls.count()).toEqual(1);
      expect(api.post).toHaveBeenCalledWith('/facebook', jasmine.any(Function));
    });
    describe('processing a single message', () => {
      var handler;
      beforeEach(() => {
        handler = api.post.calls.argsFor(0)[1];
      });
      it('should fail if x hub signature does not match', done => {
        handler({body: singleMessageTemplate, rawBody: '{"object":"page","entry":[{"id":"PAGE_ID","time":1457764198246,"messaging":[{"A":"B"}]}]}', headers: {'X-Hub-Signature': 'sha1=12345'}, env: {facebookAccessToken: 'ABC', facebookAppSecret: '54321'}})
          .catch(err => {
            expect(err).toBe('X-Hub-Signatures does not match');
            return;
          })
          .then(done, done.fail);
      });
      it('breaks down the message and puts it into the parser', () => {
        handler({body: singleMessageTemplate, env: {facebookAccessToken: 'ABC'}});
        expect(parser.calls.argsFor(0)[0]).toEqual({'A': 'B'});
      });
      it('passes the parsed value to the bot if a message can be parsed', (done) => {
        parser.and.returnValue('MSG1');
        handler({body: singleMessageTemplate, env: {}});
        Promise.resolve().then(() => {
          expect(bot).toHaveBeenCalledWith('MSG1', { body: singleMessageTemplate, env: {} });
        }).then(done, done.fail);
      });
      it('does not invoke the bot if the message cannot be parsed', (done) => {
        parser.and.returnValue(false);
        handler({body: singleMessageTemplate, env: {}}).then((message) => {
          expect(message).toBe('ok');
          expect(bot).not.toHaveBeenCalled();
        }).then(done, done.fail);
      });
      it('responds when the bot resolves', (done) => {
        parser.and.returnValue({sender: 'user1', text: 'MSG1'});
        botResolve('Yes Yes');
        handler({body: singleMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then((message) => {
          expect(message).toBe('ok');
          expect(responder).toHaveBeenCalledWith('user1', 'Yes Yes', 'ABC');
        }).then(done, done.fail);
      });
      it('can work with bot responses as strings', (done) => {
        bot.and.returnValue('Yes!');
        parser.and.returnValue({sender: 'user1', text: 'MSG1'});
        handler({body: singleMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then((message) => {
          expect(message).toBe('ok');
          expect(responder).toHaveBeenCalledWith('user1', 'Yes!', 'ABC');
        }).then(done, done.fail);

      });
      it('logs error when the bot rejects without responding', (done) => {
        parser.and.returnValue('MSG1');

        handler({body: singleMessageTemplate, env: {}}).then((message) => {
          expect(message).toBe('ok');
          expect(responder).not.toHaveBeenCalled();
          expect(logError).toHaveBeenCalledWith('No No');
        }).then(done, done.fail);

        botReject('No No');
      });
      it('logs the error when the responder throws an error', (done) => {
        parser.and.returnValue('MSG1');
        responder.and.throwError('XXX');
        botResolve('Yes');
        handler({body: singleMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then((message) => {
          expect(message).toBe('ok');
          expect(logError).toHaveBeenCalledWith(jasmine.any(Error));
        }).then(done, done.fail);
      });
      describe('working with promises in responders', () => {
        var responderResolve, responderReject, responderPromise, hasResolved;
        beforeEach(() => {
          responderPromise = new Promise((resolve, reject) => {
            responderResolve = resolve;
            responderReject = reject;
          });
          responder.and.returnValue(responderPromise);

          parser.and.returnValue('MSG1');
        });
        it('waits for the responders to resolve before completing the request', (done) => {
          handler({body: singleMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then(() => {
            hasResolved = true;
          });

          botPromise.then(() => {
            expect(hasResolved).toBeFalsy();
          }).then(done, done.fail);

          botResolve('YES');
        });
        it('resolves when the responder resolves', (done) => {
          handler({body: singleMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then((message) => {
            expect(message).toEqual('ok');
          }).then(done, done.fail);

          botPromise.then(() => {
            responderResolve('As Promised!');
          });
          botResolve('YES');
        });
        it('logs error when the responder rejects', (done) => {
          handler({body: singleMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then((message) => {
            expect(message).toEqual('ok');
            expect(logError).toHaveBeenCalledWith('Bomb!');
          }).then(done, done.fail);

          botPromise.then(() => {
            responderReject('Bomb!');
          });
          botResolve('YES');
        });
      });
    });
    describe('multiple messages', () => {
      const multiMessageTemplate = {
        'object':'page',
        'entry':[{
          'id': 'PAGE_ID',
          'time': 1457764198246,
          'messaging':[
            { 'A': 'B' },
            { 'C': 'D' }
          ]
        }, {
          'id': 'PAGE_ID',
          'time': 1457764198246,
          'messaging':[
            { 'E': 'F' },
            { 'G': 'H' }
          ]
        }]
      };
      var botPromises,
        responderPromises,
        handler,
        buildPromiseFor = (array) => {
          var pResolve, pReject, promise;
          promise = new Promise((resolve, reject) => {
            pResolve = resolve;
            pReject = reject;
          });
          promise.resolve = pResolve;
          promise.reject = pReject;
          array.push(promise);
          return promise;
        };

      beforeEach(() => {
        var index = 0;
        botPromises = [];
        responderPromises = [];
        bot.and.callFake(() => {
          return buildPromiseFor(botPromises);
        });
        responder.and.callFake(() => {
          return buildPromiseFor(responderPromises);
        });
        handler = api.post.calls.argsFor(0)[1];
        parser.and.callFake(() => {
          index += 1;
          return {
            sender: 'sender' + index,
            text: 'text' + index
          };
        });
      });
      it('parses messages in sequence', () => {
        handler({body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}});
        expect(parser.calls.count()).toBe(4);
        expect(parser.calls.argsFor(0)[0]).toEqual({'A': 'B'});
        expect(parser.calls.argsFor(1)[0]).toEqual({'C': 'D'});
        expect(parser.calls.argsFor(2)[0]).toEqual({'E': 'F'});
        expect(parser.calls.argsFor(3)[0]).toEqual({'G': 'H'});
      });
      it('calls the bot for each message individually', (done) => {
        handler({body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}});
        Promise.resolve().then(() => {
          expect(bot.calls.count()).toEqual(4);
          expect(bot).toHaveBeenCalledWith({sender: 'sender1', text: 'text1'}, {body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}});
          expect(bot).toHaveBeenCalledWith({sender: 'sender2', text: 'text2'}, {body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}});
          expect(bot).toHaveBeenCalledWith({sender: 'sender3', text: 'text3'}, {body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}});
          expect(bot).toHaveBeenCalledWith({sender: 'sender4', text: 'text4'}, {body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}});
        }).then(done, done.fail);
      });
      it('calls the responders for each bot response individually', (done) => {
        handler({body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}});
        Promise.resolve().then(() => {
          botPromises[0].resolve('From first');
          botPromises[1].resolve('From second');
          return botPromises[1];
        }).then(() => {
          expect(responder).toHaveBeenCalledWith('sender1', 'From first', 'ABC');
          expect(responder).toHaveBeenCalledWith('sender2', 'From second', 'ABC');
        }).then(done, done.fail);
      });
      it('does not resolve until all the responders resolve', (done) => {
        var hasResolved;
        handler({body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then(() => {
          hasResolved = true;
        }).then(done.fail, done.fail);
        Promise.resolve().then(() => {
          botPromises.forEach((p) => p.resolve('group'));
          return botPromises.pop();
        }).then(() => {
          responderPromises.slice(1).forEach((p) => p.resolve('result'));
          return responderPromises.pop();
        }).then(() => {
          expect(hasResolved).toBeFalsy();
        }).then(done, done.fail);
      });
      it('resolves when all the responders resolve', (done) => {
        handler({body: multiMessageTemplate, env: {facebookAccessToken: 'ABC'}}).then((message) => {
          expect(message).toEqual('ok');
        }).then(done, done.fail);
        Promise.resolve().then(() => {
          botPromises.forEach((p) => p.resolve('group'));
          return botPromises.pop();
        }).then(() => {
          responderPromises.forEach((p) => p.resolve('result'));
          return responderPromises.pop();
        });
      });
    });

  });
});

