/*global describe, it, expect, require, jasmine */
'use strict';
var reply = require('../../lib/telegram/reply'),
  https = require('https');

describe('Telegram Reply', () => {

  it('includes the telegram access token in the path request', done => {
    https.request.pipe(callOptions => {
      expect(callOptions).toEqual(jasmine.objectContaining({
        method: 'POST',
        hostname: 'api.telegram.org',
        path: '/botACCESS123/sendMessage',
        headers: {
          'Content-Type': 'application/json'
        }
      }));
      done();
    });
    reply(
      {sender: 'some123ChatId', text: 'Hello Telegram', originalRequest: {message: {}}, type: 'telegram'},
      'Hello Telegram',
      'ACCESS123'
    );
  });

  it('sends text messages as a string', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        chat_id: 'some123ChatId',
        text: 'Hello Telegram'
      });
      done();
    });
    reply(
      {sender: 'some123ChatId', originalRequest: {}},
      'Hello Telegram',
      'ACCESS123'
    );
  });

  describe('when an array is passed', () => {
    it('does not send the second request until the first one completes', done => {
      let answers = ['foo', 'bar'];
      https.request.pipe(() => {
        Promise.resolve().then(() => {
          expect(https.request.calls.length).toEqual(1);
        }).then(done);
      });
      reply(
        {sender: 'some123ChatId', originalRequest: {}, type: 'telegram'},
        answers,
        'ACCESS123'
      );
    });
    it('sends the requests in sequence', done => {
      let answers = ['foo', 'bar'];
      https.request.pipe(function () {
        this.respond('200', 'OK');
        if (https.request.calls.length === 2) {
          expect(JSON.parse(https.request.calls[0].body[0])).toEqual({chat_id:'some123ChatId',text:'foo'});
          expect(JSON.parse(https.request.calls[1].body[0])).toEqual({chat_id:'some123ChatId',text:'bar'});
          done();
        }
      });
      reply(
        {sender: 'some123ChatId', originalRequest: {}, type: 'telegram'},
        answers,
        'ACCESS123'
      );
    });

  });

  it('sends custom object as message if user provide an object without method', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        chat_id: 'some123ChatId',
        text: 'Hello *Telegram*',
        parse_mode: 'Markdown'
      });
      done();
    });

    reply(
      { sender: 'some123ChatId', text: 'Hello Telegram', originalRequest: { message: {} }, type: 'telegram' },
      { text: 'Hello *Telegram*', parse_mode: 'Markdown'},
      'ACCESS123'
    );
  });

  it('sends custom object as inline query if user provide an object without method after inline query', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        chat_id: 'some123ChatId',
        text: 'Hello *Telegram*',
        parse_mode: 'Markdown'
      });
      done();
    });

    reply(
      { sender: 'some123ChatId', text: 'Hello Telegram', originalRequest: { inline_query: {} }, type: 'telegram' },
      { text: 'Hello *Telegram*', parse_mode: 'Markdown'},
      'ACCESS123'
    );
  });

  it('sends custom object with custom method if both object and method are provided', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        chat_id: 'some123ChatId',
        text: 'Hello *Telegram*',
        parse_mode: 'Markdown'
      });
      expect(callOptions.href).toEqual('https://api.telegram.org/botACCESS123/sendMessage');
      done();
    });

    reply(
      { sender: 'some123ChatId', text: 'Hello Telegram', originalRequest: { inline_query: {} }, type: 'telegram' },
      { method: 'sendMessage', body: { text: 'Hello *Telegram*', parse_mode: 'Markdown'} },
      'ACCESS123'
    );
  });

  it('does not resolve before the https endpoint responds', done => {
    https.request.pipe(done);
    reply(
      {sender: 'some123ChatId', text: 'Hello Telegram', originalRequest: {message: {}}, type: 'telegram'},
      'Hello Telegram',
      'ACCESS123'
    ).then(done.fail, done.fail);
  });

  it('resolves when the https endpoint responds with 200', done => {
    https.request.pipe(() => {
      setTimeout(() => {
        https.request.calls[0].respond('200', 'OK', 'Hello Telegram');
      }, 10);
    });
    reply(
      {sender: 'some123ChatId', text: 'Hello Telegram', originalRequest: {message: {}}, type: 'telegram'},
      'Hello Telegram',
      'ACCESS123'
    ).then(done, done.fail);
  });

});

/*global require, describe, it, expect, beforeEach, jasmine*/
'use strict';
var setup = require('../../lib/telegram/setup');

describe('Telegram setup', () => {
  var api, bot, logError, parser, responder, botPromise, botResolve, botReject;
  beforeEach(() => {
    api = jasmine.createSpyObj('api', ['post', 'addPostDeployStep']);
    botPromise = new Promise((resolve, reject) => {
      botResolve = resolve;
      botReject = reject;
    });
    bot = jasmine.createSpy().and.returnValue(botPromise);
    logError = jasmine.createSpy();
    parser = jasmine.createSpy();
    responder = jasmine.createSpy();
    setup(api, bot, logError, parser, responder);
  });

  describe('message processor', () => {
    const singleMessageTemplate = {
      'update_id': 2837645,
      'message':{
        'message_id': 32423423,
        'from': {
          id: 12321,
          first_name: 'Testy',
          last_name: 'Lasty',
          username: 'testy_lasty'
        },
        'date': 1457764198246,
        'chat': { 'id': 123123, 'type': 'private' }
      }
    };
    it('wires the POST request for telegram to the message processor', () => {
      expect(api.post.calls.count()).toEqual(1);
      expect(api.post).toHaveBeenCalledWith('/telegram', jasmine.any(Function));
    });
    describe('processing a single message', () => {
      var handler;
      beforeEach(() => {
        handler = api.post.calls.argsFor(0)[1];
      });
      it('breaks down the message and puts it into the parser', () => {
        handler({body: singleMessageTemplate});
        expect(parser).toHaveBeenCalledWith({
          'update_id': 2837645,
          'message': {
            'message_id': 32423423,
            'from': {
              id: 12321,
              first_name: 'Testy',
              last_name: 'Lasty',
              username: 'testy_lasty'
            },
            'date': 1457764198246,
            'chat': {'id': 123123, 'type': 'private'}
          }
        });
      });
      it('passes the parsed value to the bot if a message can be parsed', (done) => {
        parser.and.returnValue('MSG1');
        handler({body: singleMessageTemplate});
        Promise.resolve().then(() => {
          expect(bot).toHaveBeenCalledWith('MSG1', {body: singleMessageTemplate});
        }).then(done, done.fail);
      });
      it('does not invoke the bot if the message cannot be parsed', (done) => {
        parser.and.returnValue(false);
        handler({body: singleMessageTemplate}).then((message) => {
          expect(message).toBe('ok');
          expect(bot).not.toHaveBeenCalled();
        }).then(done, done.fail);
      });
      it('responds when the bot resolves', (done) => {
        parser.and.returnValue({sender: 'user1', text: 'MSG1'});
        botResolve('Yes Yes');
        handler({body: singleMessageTemplate, env: {telegramAccessToken: 'some123AccessToken'}}).then(() => {
          expect(responder).toHaveBeenCalledWith({sender: 'user1', text: 'MSG1'}, 'Yes Yes', 'some123AccessToken');
        }).then(done, done.fail);
      });
      it('can work with bot responses as strings', (done) => {
        bot.and.returnValue('Yes!');
        parser.and.returnValue({sender: 'user1', text: 'MSG1'});
        handler({body: singleMessageTemplate, env: {telegramAccessToken: 'some123AccessToken'}}).then(() => {
          expect(responder).toHaveBeenCalledWith({sender: 'user1', text: 'MSG1'}, 'Yes!', 'some123AccessToken');
        }).then(done, done.fail);

      });
      it('logs error when the bot rejects without responding', (done) => {
        parser.and.returnValue('MSG1');

        handler({body: singleMessageTemplate}).then(() => {
          expect(responder).not.toHaveBeenCalled();
          expect(logError).toHaveBeenCalledWith('No No');
        }).then(done, done.fail);

        botReject('No No');
      });
      it('logs the error when the responder throws an error', (done) => {
        parser.and.returnValue('MSG1');
        responder.and.throwError('XXX');
        botResolve('Yes');
        handler({body: singleMessageTemplate, env: {telegramAccessToken: 'some123AccessToken'}}).then(() => {
          expect(logError).toHaveBeenCalledWith(jasmine.any(Error));
        }).then(done, done.fail);
      });
      describe('working with promises in responders', () => {
        var responderResolve, responderReject, responderPromise, hasResolved;
        beforeEach(() => {
          responderPromise = new Promise((resolve, reject) => {
            responderResolve = resolve;
            responderReject = reject;
          });
          responder.and.returnValue(responderPromise);

          parser.and.returnValue('MSG1');
        });
        it('waits for the responders to resolve before completing the request', (done) => {
          handler({body: singleMessageTemplate, env: {telegramAccessToken: 'some123AccessToken'}}).then(() => {
            hasResolved = true;
          });

          botPromise.then(() => {
            expect(hasResolved).toBeFalsy();
          }).then(done, done.fail);

          botResolve('YES');
        });
        it('resolves when the responder resolves', (done) => {
          handler({body: singleMessageTemplate, env: {telegramAccessToken: 'some123AccessToken'}}).then(done, done.fail);

          botPromise.then(() => {
            responderResolve('As Promised!');
          });
          botResolve('YES');
        });
        it('logs error when the responder rejects', (done) => {
          handler({body: singleMessageTemplate, env: {telegramAccessToken: 'some123AccessToken'}}).then(() => {
            expect(logError).toHaveBeenCalledWith('Bomb!');
          }).then(done, done.fail);

          botPromise.then(() => {
            responderReject('Bomb!');
          });
          botResolve('YES');
        });
      });
    });
  });
});

