/* global describe, it, expect, require */
'use strict';
var parse = require('../../lib/viber/parse');

describe('Viber parse', () => {
  it('should not return anything if the format is invalid', () => {
    expect(parse('Just a string')).toBeUndefined();
    expect(parse()).toBeUndefined();
    expect(parse(false)).toBeUndefined();
    expect(parse(123)).toBeUndefined();
    expect(parse({})).toBeUndefined();
    expect(parse([1, 2, 3])).toBeUndefined();
  });
  it('should not return anything if message is missing', () => {
    expect(parse({sender: { id: 1 }})).toBeUndefined();
  });
  it('should return a parsed object if message is in correct format', () => {
    var msg = {
      event: 'message',
      timestamp: new Date().getTime(),
      message_token: 123,
      sender: {
        id: 'ABC',
        name: 'Claudia',
        avatar: 'https://claudiajs.com/assets/claudiajs.png'
      },
      message: {
        text: 'Hello',
        type: 'text'
      }
    };
    expect(parse(msg)).toEqual({
      sender: msg.sender.id,
      text: msg.message.text,
      originalRequest: msg,
      type: 'viber'
    });
  });
  it('should return a parsed object with an empty text if message is not textual', () => {
    var msg = {
      event: 'message',
      timestamp: new Date().getTime(),
      message_token: 123,
      sender: {
        id: 'ABC',
        name: 'Claudia',
        avatar: 'https://claudiajs.com/assets/claudiajs.png'
      },
      message: {
        type: 'image'
      }
    };
    expect(parse(msg)).toEqual({
      sender: msg.sender.id,
      text: '',
      originalRequest: msg,
      type: 'viber'
    });
  });
});

/*global describe, it, expect, require, jasmine, beforeEach */
var reply = require('../../lib/viber/reply'),
  https = require('https');
describe('Viber Reply', () => {
  'use strict';
  beforeEach(() =>{
    jasmine.DEFAULT_TIMEOUT_INTERVAL = 5000;
  });

  it('should include the token in the request', done => {
    https.request.pipe(callOptions => {
      expect(callOptions).toEqual(jasmine.objectContaining({
        protocol: 'https:',
        hostname: 'chatapi.viber.com',
        path: '/pa/send_message',
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Content-Length': 79
        },
        body: '{"type":"text","auth_token":"ACCESS123","text":"Hi there","receiver":"user123"}'
      }));
      done();
    });
    reply('user123', 'Hi there', 'ACCESS123');
  });
  it('should send a string message as a text object', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        type: 'text',
        auth_token: 'ACCESS123',
        text: 'Hi there',
        receiver: 'user123'
      });
      done();
    });
    reply('user123', 'Hi there', 'ACCESS123');
  });
  describe('when an array is passed', () => {
    it('should not send the second request until the first one completes', done => {
      let answers = ['foo', 'bar'];
      https.request.pipe(() => {
        Promise.resolve().then(() => {
          expect(https.request.calls.length).toEqual(1);
        }).then(done);
      });
      reply('user123', answers, 'ACCESS123');
    });
    it('should send the requests in sequence', done => {
      let answers = ['foo', 'bar'];
      https.request.pipe(function () {
        this.respond('200', 'OK');
        if (https.request.calls.length === 2) {
          expect(JSON.parse(https.request.calls[0].body[0])).toEqual({
            type: 'text',
            auth_token: 'ACCESS123',
            text: 'foo',
            receiver: 'user123'
          });
          expect(JSON.parse(https.request.calls[1].body[0])).toEqual({
            type: 'text',
            auth_token: 'ACCESS123',
            text: 'bar',
            receiver: 'user123'
          });
          done();
        }
      });
      reply('user123', answers, 'ACCESS123');
    });

  });

  it('should send complex messages without transforming into a text object', done => {
    https.request.pipe(callOptions => {
      expect(JSON.parse(callOptions.body)).toEqual({
        auth_token: 'ACCESS123',
        receiver: 'user123',
        tracking_data: 123,
        type: 'text',
        text: 'random text message'
      });
      done();
    });
    reply('user123', {
      tracking_data: 123,
      type: 'text',
      text: 'random text message'
    }, 'ACCESS123');
  });
  it('should not resolve before the https endpoint responds', done => {
    https.request.pipe(done);
    reply('user123', {template: 'big', contents: { title: 'red'} }, 'ACCESS123').then(done.fail, done.fail);
  });
  it('should resolve when the https endpoint responds with 200', done => {
    https.request.pipe(function () {
      this.respond('200', 'OK', 'Hi there');
    });
    reply('user123', {template: 'big', contents: { title: 'red'} }, 'ACCESS123').then(done, done.fail);
  });
});

/*global require, describe, it, expect, beforeEach, jasmine*/
'use strict';
var underTest = require('../../lib/viber/setup');
describe('Viber setup', () => {
  var api, bot, logError, parser, responder, botPromise, botResolve, botReject;
  beforeEach(() => {
    api = jasmine.createSpyObj('api', ['post', 'addPostDeployStep']);
    botPromise = new Promise((resolve, reject) => {
      botResolve = resolve;
      botReject = reject;
    });
    bot = jasmine.createSpy().and.returnValue(botPromise);
    parser = jasmine.createSpy();
    logError = jasmine.createSpy();
    responder = jasmine.createSpy();
    underTest(api, bot, logError, parser, responder);
  });
  describe('message processor', () => {
    const singleMessageTemplate = {
      event: 'message',
      timestamp: 1477292830416,
      message_token: 12345,
      sender: {
        id: 'abc',
        name: 'Claudia',
        avatar: 'https://example.com/path/to.image'
      },
      'message': {
        text: 'Hello',
        type: 'text'
      }
    };
    it('shoyld wire the POST request for Viber to the message processor', () => {
      expect(api.post.calls.count()).toEqual(1);
      expect(api.post).toHaveBeenCalledWith('/viber', jasmine.any(Function));
    });
    describe('processing a single message', () => {
      var handler;
      beforeEach(() => {
        handler = api.post.calls.argsFor(0)[1];
      });
      it('should break down the message and put it into the parser', () => {
        handler({ body: singleMessageTemplate, env: { viberAccessToken: 'ABC' } });
        expect(parser).toHaveBeenCalledWith(singleMessageTemplate);
      });
      it('should pass the parsed value to the bot if a message can be parsed', (done) => {
        parser.and.returnValue('MSG1');
        handler({body: singleMessageTemplate, env: {}});
        Promise.resolve().then(() => {
          expect(bot).toHaveBeenCalledWith('MSG1', { body: singleMessageTemplate, env: {} });
        }).then(done, done.fail);
      });
      it('should not invoke the bot if the message cannot be parsed', (done) => {
        parser.and.returnValue(false);
        handler({body: singleMessageTemplate, env: {}}).then((message) => {
          expect(message).toBe('ok');
          expect(bot).not.toHaveBeenCalled();
        }).then(done, done.fail);
      });
      it('should respond when the bot resolves', (done) => {
        parser.and.returnValue({sender: 'user1', text: 'MSG1'});
        handler({ body: singleMessageTemplate, env: { viberAccessToken: 'ABC' } }).then(() => {
          expect(responder).toHaveBeenCalledWith('user1', 'Yes Yes', 'ABC');
        }).then(done, done.fail);

        botResolve('Yes Yes');
      });
      it('should work with bot responses as strings', (done) => {
        bot.and.returnValue('Yes!');
        parser.and.returnValue({sender: 'user1', text: 'MSG1'});
        handler({body: singleMessageTemplate, env: {viberAccessToken: 'ABC'}}).then(() => {
          expect(responder).toHaveBeenCalledWith('user1', 'Yes!', 'ABC');
        }).then(done, done.fail);

      });
      it('should log an error when the bot rejects without responding', (done) => {
        parser.and.returnValue('MSG1');

        handler({body: singleMessageTemplate, env: {}}).then(() => {
          expect(responder).not.toHaveBeenCalled();
          expect(logError).toHaveBeenCalledWith('No No');
        }).then(done, done.fail);

        botReject('No No');
      });
      it('should log an error when the responder throws an error', (done) => {
        parser.and.returnValue('MSG1');
        responder.and.throwError('XXX');
        botResolve('Yes');
        handler({body: singleMessageTemplate, env: {viberAccessToken: 'ABC'}}).then(() => {
          expect(logError).toHaveBeenCalledWith(jasmine.any(Error));
        }).then(done, done.fail);
      });
      describe('should work with promises in responders', () => {
        var responderResolve, responderReject, responderPromise, hasResolved;
        beforeEach(() => {
          responderPromise = new Promise((resolve, reject) => {
            responderResolve = resolve;
            responderReject = reject;
          });
          responder.and.returnValue(responderPromise);

          parser.and.returnValue('MSG1');
        });
        it('should wait for the responders to resolve before completing the request', (done) => {
          handler({body: singleMessageTemplate, env: {viberAccessToken: 'ABC'}}).then(() => {
            hasResolved = true;
          });

          botPromise.then(() => {
            expect(hasResolved).toBeFalsy();
          }).then(done, done.fail);

          botResolve('YES');
        });
        it('should resolve when the responder resolves', (done) => {
          handler({body: singleMessageTemplate, env: {viberAccessToken: 'ABC'}}).then((message) => {
            expect(message).toEqual('As Promised!');
          }).then(done, done.fail);

          botPromise.then(() => {
            responderResolve('As Promised!');
          });
          botResolve('YES');
        });
        it('should log error when the responder rejects', (done) => {
          handler({body: singleMessageTemplate, env: {viberAccessToken: 'ABC'}}).then(() => {
            expect(logError).toHaveBeenCalledWith('Bomb!');
          }).then(done, done.fail);

          botPromise.then(() => {
            responderReject('Bomb!');
          });
          botResolve('YES');
        });
      });
    });

  });
});
